/**
 * 对象扩展
 *
 * @public
 * @param {Object} target 目标对象
 * @return {Object}
 */
module.exports = function (target) {
    var args = Array.prototype.slice.call(arguments, 1);

    args.forEach(function (obj) {
        obj = obj || {};
        for (var key in obj) {
            if (obj.hasOwnProperty(key)) {
                target[key] = obj[key];
            }
        }
    });

    return target;
};
